      return res.status(404).send({
        success: false,
        message: "User not found or wrong password",
      });
    }

    // generate token
    const token = jwt.sign({ id: user._id }, process.env.JWT_SECRET, {
      expiresIn: "1h",
    });
    user.password = undefined;
    res.status(200).send({
      success: true,
      message: "User logged in successfully",
